import React, { lazy, Suspense, useState, useEffect } from 'react'
import { Route, Routes } from 'react-router-dom'
import AllMovie from '../components/AllMovie/AllMovie.jsx'

const Home = lazy(() => import('../pages/Home.jsx'));

const MovieDetail = lazy(() => import('../pages/detail/MovieDetail.jsx'));

const Loading = () => {
  return (
    <div className="flex justify-center items-center h-screen bg-[rgba(15,15,15)]">
      <div className="border-4 border-t-4 border-gray-200 rounded-full w-12 h-12 animate-spin"></div>
    </div>
  );
};

const AllMovieLazy = lazy(() => import('../components/AllMovie/AllMovie.jsx'));

const Content = () => {
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    setIsLoading(true);
  }, []);

  return (
    <Routes >
      <Route path="/" element={
        <Suspense fallback={<Loading />}>
          <Home />
        </Suspense>
      } />
      <Route path="/:category/:id" element={
        <Suspense fallback={<Loading />}>
          <MovieDetail />
        </Suspense>
      } />
      <Route path="/:category" element={
        <Suspense fallback={<Loading />}>
          <AllMovieLazy />
        </Suspense>
      } />
      <Route path="/:category/search/:kw" element={
        <Suspense fallback={<Loading />}>
          <AllMovie />
        </Suspense>
      } />
    </Routes>
  );
}

export default Content;
