import React, { useEffect, useState } from 'react'
import movieAPI from '../../api/movieAPI'
const Banner = () => {
    const [movie,setMovie] = useState([])
    useEffect(() => {
        const getList = async () => {
            const data = await movieAPI.getMovieList('popular').then(results => results.results)
            setMovie(data[Math.floor(Math.random() * (data.length - 1))])
        }
        getList()
        
    }, [])

    console.log(movie)
    const POSTER_PATH = `https://image.tmdb.org/t/p/original${movie.poster_path}`
    return (
        <div className='w-[100vw] h-screen bg-center bg-no-repeat bg-cover relative' style = {{backgroundImage: `url(https://image.tmdb.org/t/p/original${movie.backdrop_path})`}}>
            <div className='flex items-center absolute top-1/2 px-8' style = {{transform: 'translateX(50%)'}}>
                <div className='mr-6'>
                    <h3 className='text-4xl text-white font-bold'>{movie.title}</h3>
                    <p className='mt-6 text-white'>{movie.overview}</p>
                </div>
                <div>
                    <img src = {POSTER_PATH} alt = 'movie'/>
                </div>
            </div>
        </div>
    )
}

export default Banner
