import React, { useEffect, useState } from 'react'
import movieAPI from '../../api/movieAPI'
const Banner = () => {
    const [movie,setMovie] = useState([])
    useEffect(() => {
        const getList = async () => {
            const data = await movieAPI.getMovieList('popular').then(results => results.results)
            setMovie(data[Math.floor(Math.random() * (data.length - 1))])
        }
        getList()
        
    }, [])

    console.log(movie)
    return (
        <div className='w-[50vw] h-[50vh]' styles = {{backgroundImage: `url(https://image.tmdb.org/t/p/original${movie.backdrop_path})`}}>
            <div>
                <h3>{movie.title}</h3>
            </div>
        </div>
    )
}

export default Banner
