import React, { useEffect, useState } from 'react'
import movieAPI from '../../api/movieAPI'
import { SwiperSlide, Swiper } from 'swiper/react';
import MovieCard from '../MovieCard/MovieCard';
const MovieList = (props) => {
    const {similar, id} = props
    const [movies, setMovies] = useState([])

    useEffect(() => {
        const getMovies = async () => {
            let data = null;
            if(id){
                if(similar) {
                data = await movieAPI.getSimilarVideo('movie', id).then(result => result.results)
                } else {
                    data = await movieAPI.getMovieList('topRated').then(results => results.results)
                }
            }
            setMovies(data)
        }
        getMovies()
    }, [similar,id])
    console.log(movies)
  return (
    <div>
        <Swiper
            grabCursor = {true}
            spaceBetween = {10}
            slidesPerView = {'auto'}
        >
            {movies.map(item => {
                return (
                    <SwiperSlide key = {id}>
                        <MovieCard item = {item}/>
                    </SwiperSlide>
                )
            })}
        </Swiper>
    </div>
  )
}

export default MovieList
