import React, { useEffect, useState } from 'react'
import movieAPI from '../../api/movieAPI'
import { Swiper, SwiperSlide } from 'swiper/react';
import MovieCard from '../MovieCard/MovieCard';
import './movielist.scss'
const MovieList = (props) => {
    const {similar, id} = props
    const [movies, setMovies] = useState([])

    useEffect(() => {
        const getMovies = async () => {
            let data = [];
            if(id){
                if(similar) {
                    data = await movieAPI.getSimilarVideo('movie', id).then(result => result.results)
                } else {
                    data = await movieAPI.getMovieList('topRated').then(results => results.results)
                }
            }
            setMovies(data)
        }
        getMovies()
    }, [similar,id])
    console.log(movies)
  return (
    <div>
        {/* <Swiper
            grabCursor={true}
            spaceBetween={10}
            slidesPerView={'auto'}
        >
            <div>123</div>
            {movies.map(item => {
                return (
                    <MovieCard key = {item.id} item = {item}/>
                )
            })}
        </Swiper> */}
        <Swiper
        slidesPerView={3}
        spaceBetween={30}
        pagination={{
          clickable: true,
        }}
        className = 'flex'
      >
          <div>123</div>
        {
            movies.map(item => {
                return (
                    <SwiperSlide>
                        <MovieCard key = {item.id} item = {item}/>
                    </SwiperSlide>
                )
            })
        }
      </Swiper>
    </div>
  )
}

export default MovieList
