import React, { useEffect, useState } from 'react'
import movieAPI from '../../../api/movieAPI'
const DetailVideos = (props) => {

    const [videos,setVideos] = useState([])

    useEffect(() => {
        const getVideos = async () => {
            if(props.id) {
                const data = await movieAPI.getOtherVideo('movie', props.id)
                setVideos(data.results.slice(0,5))
            }
        }
        getVideos()
        
    }, [props.id])
  return (
    <div className='w-[1300px] m-[auto] min-h-[900px]'>
        {videos.map(item => <Video key = {item.id} item = {item}/>)}
    </div>
  )
}

export const Video = (props) => {
    const {item} = props
    return (
        <>
            <div className='h-[100%]'>
                <div>
                    <h2>{item.name}</h2>
                </div>
                <iframe width='100%' height='100%' title = 'video' src = {`https://www.youtube.com/embed/${item.key}`}></iframe>
            </div>
        </>
    )
}

export default DetailVideos
