import React, { useEffect, useState } from 'react'
import movieAPI from '../../../api/movieAPI'
const Credits = (props) => {
    const {id} = props
    const [casts, setCasts] = useState([])
    useEffect(() => {
        const getCasts = async () => {
            const data = await movieAPI.getCredits('movie',id)
            setCasts(data)
        }
        getCasts()
    }, [id])
    casts = casts.cast ? casts.cast.slice(0,5) : []
  return (
    <div>Credits</div>
  )
}

export default Credits
